import React from "react";
import { View, Text } from "react-native";
const Result = ({title,subtitle,stars,descri}) => {
    return (
        <View>
            <Text style={{backgroundColor:"blue", width:"100%", height:"50%",textAlign:"center", color:"white", fontSize:32}}>{title}</Text>
            <View style={{backgroundColor:"black", justifyContent:"center", alignItems:"center"}}>
                    <Text style={{textAlign:"center", color:"white"}}>{subtitle}</Text>
                    <Text style={{textAlign:"center", color:"white"}}>{descri}</Text>
                    <Text style={{textAlign:"center", color:"white"}}>{stars} Ã©toiles</Text>
            </View>
        </View>
    );
};

export default Result;
