import React, { useState, useEffect } from 'react';
import { Provider } from 'react-redux';
import { TouchableOpacity, Text, View, StyleSheet } from 'react-native';
import store from './Store';
import AnimalList from './Components/AnimalList';
import { addAnimal } from './Crud'; 
import AddAnimalForm from './Components/AddAnimalForm';

const ListeAnimal = ({ navigation }) => {
  const [authenticated, setAuthenticated] = useState(false);
  const [add, setAdd] = useState(false);

  useEffect(() => {
    const isAuthenticated = localStorage.getItem('authenticated');
    if (!isAuthenticated) {
      navigation.navigate('Login');
    } else {
      setAuthenticated(true);
      store.dispatch(
        addAnimal(
          '1',
          'Dog',
          'Labrador',
          'Brown',
          'https://images.rawpixel.com/image_png_800/czNmcy1wcml2YXRlL3Jhd3BpeGVsX2ltYWdlcy93ZWJzaXRlX2NvbnRlbnQvcHUyMzMxNzg4LWltYWdlLXJtNTAzLTAxXzEtbDBqOXFyYzMucG5n.png'
        )
      );
      store.dispatch(
        addAnimal(
          '2',
          'Cat',
          'Siamese',
          'White',
          'https://images.rawpixel.com/image_png_800/czNmcy1wcml2YXRlL3Jhd3BpeGVsX2ltYWdlcy93ZWJzaXRlX2NvbnRlbnQvcHUyMzMxNjM2LWltYWdlLTAxLXJtNTAzXzMtbDBqOXFrNnEucG5n.png'
        )
      );
    }
  }, []);

  return (
    <Provider store={store}>
      <AnimalList />
      <TouchableOpacity
        style={styles.addButton}
        onPress={() => {
          setAdd(true);
        }}>
        <Text style={styles.buttonText}>+</Text>
      </TouchableOpacity>
      {add && (
        <View style={styles.overlay}>
                <TouchableOpacity
        style={styles.addButton}
        onPress={() => {
          setAdd(true);
        }}>
        <Text style={styles.buttonText}></Text>
      </TouchableOpacity>
          <AddAnimalForm onClose={() => setAdd(false)} />
        </View>
      )}
    </Provider>
  );
};

const styles = StyleSheet.create({
  addButton: {
    position: 'absolute',
    bottom: 20,
    right: 20,
    backgroundColor: 'rgb(239, 135, 11)',
    padding: 20,
    borderRadius: 100,
  },
  buttonText: {
    color: 'white',
    fontSize: 24,
    opacity:1
  },
  overlay: {
    ...StyleSheet.absoluteFillObject,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    justifyContent: 'center',
    alignItems: 'center',
  },
});

export default ListeAnimal;
