import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet, TouchableOpacity, Text, Image } from 'react-native';
import { connect } from 'react-redux';
import { addAnimal } from '../Crud';
import * as ImagePicker from "expo-image-picker";

const AddAnimalForm = ({ dispatch, onClose }) => {
  const [nom, setNom] = useState('');
  const [type, setType] = useState('');
  const [couleur, setCouleur] = useState('');
  const [image, setImage] = useState(null); 

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: false,
      aspect: [4, 3],
      quality: 1,
      base64: false,
    });

    console.log(result)
    if (!result.cancelled) {
      setImage(result.assets[0].uri);
    }
  };

  const handleSubmit = async() => {
    console.log("lasa")
    try {
      const response = await fetch(image);
      console.log(response)

      const blob = await response.blob();

      dispatch(addAnimal(nom, type, couleur, blob));
      setNom('');
      setType('');
      setCouleur('');
      setImage(null); 
      onClose(); 
    } catch (error) {
      console.error(error);
    }

  };

  return (
    <View style={styles.container}>
      <TextInput style={styles.input} placeholder="Nom" value={nom} onChangeText={setNom} />
      <TextInput style={styles.input} placeholder="Type" value={type} onChangeText={setType} />
      <TextInput style={styles.input} placeholder="Couleur" value={couleur} onChangeText={setCouleur} />
      <TouchableOpacity
        style={styles.imageButton}
        onPress={pickImage}
      >
        <Text style={styles.imageButtonText}>SÃ©lectionner une photo</Text>
      </TouchableOpacity>
      {image && (
        <Image
          source={{ uri: image }}
          style={styles.imagePreview}
        />
      )}
      <TouchableOpacity style={styles.editButton} onPress={handleSubmit}>
        <Text>Ajouter</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 10,
  },
  input: {
    borderWidth: 1,
    borderColor: 'gray',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
    alignItems: 'center',
    justifyContent: 'center',
  },
  editButton: {
    backgroundColor: 'orange',
    borderRadius: 5,
    padding: 5,
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default connect()(AddAnimalForm);
