export const ADD_ANIMAL = 'ADD_ANIMAL';
export const DELETE_ANIMAL = 'DELETE_ANIMAL';
export const UPDATE_ANIMAL = 'UPDATE_ANIMAL';

export const addAnimal = (id, nom, type, couleur, photo) => ({
  type: ADD_ANIMAL,
  payload: { id, nom, type, couleur, photo },
});

export const deleteUser = (id) => ({
  type: DELETE_ANIMAL,
  payload: { id },
});

export const updateUser = (id, newData) => ({
  type: UPDATE_ANIMAL,
  payload: { nom, newData },
});
