import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Dimensions } from 'react-native';

const Examen = () => {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');
  const [screenWidth, setScreenWidth] = useState(Dimensions.get('window').width);

  useEffect(() => {
    const updateLayout = () => {
      setScreenWidth(Dimensions.get('window').width);
    };

    Dimensions.addEventListener('change', updateLayout);

    return () => {
      Dimensions.removeEventListener('change', updateLayout);
    };
  }, []);

  const handleOperation = (operation) => {
    switch (operation) {
      case 'ln':
        setResult(Math.log(parseFloat(input)).toString());
        break;
      case 'exp':
        setResult(Math.exp(parseFloat(input)).toString());
        break;
      case 'sqrt':
        setResult(Math.sqrt(parseFloat(input)).toString());
        break;
      case 'x^2':
        setResult((parseFloat(input) ** 2).toString());
        break;
      case '^':
        setInput(input + '**');
        break;
      case '(':
        setInput(input + '(');
        break;
      case ')':
        setInput(input + ')');
        break;
      case 'C':
        setInput('');
        setResult('');
        break;
      case '=':
        try {
          setResult(eval(input).toString());
        } catch (error) {
          setResult('Error');
        }
        break;
      default:
        setInput(input + operation);
        break;
    }
  };  

  return (
    <View style={styles.container}>
      <TextInput
        style={[styles.input, { width: screenWidth * 0.8 }]}
        value={input}
        onChangeText={(text) => setInput(text)}
        keyboardType="numeric"
      />
      <View style={styles.row}>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('ln')}>
          <Text style={styles.buttonText}>ln</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('exp')}>
          <Text style={styles.buttonText}>e</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('sqrt')}>
          <Text style={styles.buttonText}>sqrt</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('^')}>
          <Text style={styles.buttonText}>^</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.row}>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('7')}>
          <Text style={styles.buttonText}>7</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('8')}>
          <Text style={styles.buttonText}>8</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('9')}>
          <Text style={styles.buttonText}>9</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('/')}>
          <Text style={styles.buttonText}>/</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.row}>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('4')}>
          <Text style={styles.buttonText}>4</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('5')}>
          <Text style={styles.buttonText}>5</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('6')}>
          <Text style={styles.buttonText}>6</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('*')}>
          <Text style={styles.buttonText}>*</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.row}>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('1')}>
          <Text style={styles.buttonText}>1</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('2')}>
          <Text style={styles.buttonText}>2</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('3')}>
          <Text style={styles.buttonText}>3</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('-')}>
          <Text style={styles.buttonText}>-</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.row}>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('0')}>
          <Text style={styles.buttonText}>0</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('.')}>
          <Text style={styles.buttonText}>.</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('=')}>
          <Text style={styles.buttonText}>=</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('+')}>
          <Text style={styles.buttonText}>+</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.row}>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('(')}>
          <Text style={styles.buttonText}>(</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation(')')}>
          <Text style={styles.buttonText}>)</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('x^2')}>
          <Text style={styles.buttonText}>x^2</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleOperation('C')}>
          <Text style={styles.buttonText}>AC</Text>
        </TouchableOpacity>
      </View>
      <Text style={styles.result}>{result}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  input: {
    borderWidth: 1,
    borderColor: '#000',
    padding: 10,
    marginBottom: 10,
    textAlign: 'right',
  },
  row: {
    flexDirection: 'row',
    marginBottom: 10,
    justifyContent: 'space-around', // Adjusted to distribute buttons evenly
    width: '100%', 
  },
  button: {
    backgroundColor: '#DDDDDD',
    padding: 20,
    margin: 5,
    alignItems: 'center',
    justifyContent: 'center',
    width: '20%', 
  },
  buttonText: {
    fontSize: 20,
  },
  result: {
    fontSize: 24,
    marginTop: 20,
  },
});

export default Examen;
