import React, { useState } from 'react';
import { View, TouchableOpacity, Text, StyleSheet } from 'react-native';

// Définition des cartes
const cards = [
  { id: 0, value: 1, suit: 'carreau' },
  { id: 1, value: 2, suit: 'carreau' },
  { id: 2, value: 3, suit: 'carreau' },
  { id: 3, value: 4, suit: 'carreau' },
  { id: 4, value: 5, suit: 'carreau' },
  { id: 5, value: 6, suit: 'carreau' },
  { id: 6, value: 7, suit: 'carreau' },
  { id: 7, value: 8, suit: 'carreau' }
];

const App = () => {
  const [cardsState, setCardsState] = useState(cards);

  // Fonction pour déplacer les cartes
  const moveCards = () => {
    setCardsState(prevCards => {
      const movedCards = [...prevCards];
      // Déplacer chaque carte d'une position vers la gauche (en bouclant)
      for (let i = 0; i < movedCards.length; i++) {
        const temp = movedCards[i].value;
        movedCards[i].value = movedCards[i].value === 1 ? 7 : temp + 1;
      }
      return movedCards;
    });
  };

  // Fonction pour changer la famille d'une carte
  const changeSuit = (id) => {
    setCardsState(prevCards => {
      const updatedCards = prevCards.map(card => {
        if (card.id === id) {
          switch (card.suit) {
            case 'carreau':
              card.suit = 'coeur';
              break;
            case 'coeur':
              card.suit = 'pique';
              break;
            case 'pique':
              card.suit = 'trèfle';
              break;
            case 'trèfle':
              card.suit = 'carreau';
              break;
            default:
              break;
          }
        }
        return card;
      });
      return updatedCards;
    });
  };

  return (
    <View style={styles.container}>
      <View style={styles.table}>
        {cardsState.map(card => (
          <TouchableOpacity key={card.id} onPress={() => changeSuit(card.id)}>
            <View style={styles.card}>
              <Text>{card.value}</Text>
              <Text>{card.suit}</Text>
            </View>
          </TouchableOpacity>
        ))}
      </View>
      <TouchableOpacity onPress={moveCards}>
        <View style={styles.centerCell}></View>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff'
  },
  table: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    width: 240
  },
  card: {
    width: 80,
    height: 120,
    justifyContent: 'center',
    alignItems: 'center',
    borderWidth: 1,
    borderColor: 'black',
    margin: 5
  },
  centerCell: {
    width: 80,
    height: 120,
    backgroundColor: 'red',
    margin: 5
  }
});

export default TpCart2;
