import React, { useState } from 'react';
import { View, TextInput, Button } from 'react-native';
import { connect } from 'react-redux';
import { addUser } from '../Crud';

const AddUserForm = ({ dispatch }) => {
  const [id, setId] = useState('');
  const [nom, setNom] = useState('');
  const [prenom, setPrenom] = useState('');
  const [age, setAge] = useState('');
  const [usernom, setUsernom] = useState('');

  const handleSubmit = () => {
    dispatch(addUser(id, nom, prenom, age, usernom));
    setId('');
    setNom('');
    setPrenom('');
    setAge('');
    setUsernom('');
  };

  return (
    <View>
      <TextInput placeholder="id" value={id} onChangeText={setNom} />
      <TextInput placeholder="Nom" value={nom} onChangeText={setNom} />
      <TextInput placeholder="PrÃ©nom" value={prenom} onChangeText={setPrenom} />
      <TextInput placeholder="Age" value={age} onChangeText={setAge} />
      <TextInput placeholder="Usernom" value={usernom} onChangeText={setUsernom} />
      <Button title="Add User" onPress={handleSubmit} />
    </View>
  );
};

export default connect()(AddUserForm);
