import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet, TouchableOpacity } from 'react-native';
import { connect } from 'react-redux';
import { addAnimal } from '../Crud';

const AddAnimalForm = ({ dispatch, onClose }) => {
  const [nom, setNom] = useState('');
  const [type, setType] = useState('');
  const [couleur, setCouleur] = useState('');
  const [image, setImage] = useState('');

  const handleSubmit = () => {
    dispatch(addAnimal(nom, type, couleur, image));
    setNom('');
    setType('');
    setCouleur('');
    setImage('');
    onClose(); // Call onClose to close the form after submission
  };

  return (
    <View style={styles.container}>
      <TextInput style={styles.input} placeholder="Nom" value={nom} onChangeText={setNom} />
      <TextInput style={styles.input} placeholder="Type" value={type} onChangeText={setType} />
      <TextInput style={styles.input} placeholder="Couleur" value={couleur} onChangeText={setCouleur} />
      <TextInput style={styles.input} placeholder="Image" value={image} onChangeText={setImage} />
      <TouchableOpacity style={styles.editButton} onPress={handleSubmit}>
        <Text>Ajouter</Text>  
      </TouchableOpacity> 
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 10,
  },
  input: {
    borderWidth: 1,
    borderColor: 'gray',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
});

export default connect()(AddAnimalForm);
