// ListeAnimal.js
import React, { useState, useEffect } from 'react';
import { Provider } from 'react-redux';
import { TouchableOpacity, Text } from 'react-native';
import store from './Store';
import AnimalList from './Components/AnimalList';
import { addAnimal } from './Crud'; // Import the addAnimal action

const ListeAnimal = ({ navigation }) => {
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    const isAuthenticated = localStorage.getItem('authenticated');
    if (!isAuthenticated) {
      navigation.navigate('Login');
    } else {
      setAuthenticated(true);
      store.dispatch(addAnimal('1', 'Dog', 'Labrador', 'Brown', 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.rawpixel.com%2Fsearch%2Fdog&psig=AOvVaw0PpE6iJbTNVAprgQ86P7n9&ust=1712131327715000&source=images&cd=vfe&opi=89978449&ved=0CBIQjRxqFwoTCMDO6MWHo4UDFQAAAAAdAAAAABAE'));
      store.dispatch(addAnimal('2', 'Cat', 'Siamese', 'White', 'https://example.com/cat.jpg'));
    }
  }, []);

  return (
    <Provider store={store}>
      <AnimalList />
      <TouchableOpacity
        style={{ position: 'absolute', bottom: 20, right: 20, backgroundColor: 'rgb(239, 135, 11)', padding: 20, borderRadius: 100 }}
        onPress={() => {
        }}>
        <Text style={{ color: 'white', fontSize: 24 }}>+</Text>
      </TouchableOpacity>
    </Provider>
  );
};

export default ListeAnimal;
