import React from 'react';
import { View, Text, FlatList, Button } from 'react-native';
import { connect } from 'react-redux';
import { deleteUser } from '../Crud';

const UserList = ({ users, dispatch }) => {
  return (
    <View>
      <FlatList
        data={users}
        renderItem={({ item }) => (
<table>
                    <tr style={Test.id % 2 === 0 ? styles.grey : styles.white}>
                        <td><Text style={styles.textes}>{Test.nom}</Text></td>
                        <td><Text style={styles.textes}>{Test.prenom}</Text></td>
                        <td><Text style={styles.textes}>{Test.nomUser}</Text></td>
                        <td><Text style={styles.textes}>{Test.age}</Text></td>
                        <td>
                        <Button title="Delete" onPress={() => dispatch(deleteUser(item.usernom))} />
                        </td>
                    </tr>
                </table>
        )}
        keyExtractor={(item) => item.usernom}
      />
    </View>
  );
};

const mapStateToProps = (state) => ({
  users: state.users,
});

export default connect(mapStateToProps)(UserList);
