import React, { useEffect } from 'react';
import { Provider } from 'react-redux';
import { TouchableOpacity, Text } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer, useNavigation } from '@react-navigation/native';
import store from './Store';
import AnimalList from './Components/AnimalList';
import AddAnimalForm from './Components/AddAnimalForm';
import { addAnimal } from './Crud';

const Stack = createStackNavigator();

const ListeAnimal = () => {
  const navigation = useNavigation(); // Use useNavigation hook

  useEffect(() => {
    const isAuthenticated = localStorage.getItem('authenticated');
    if (!isAuthenticated) {
      navigation.navigate('Login');
    }
  }, []);

  const handleAddAnimalPress = () => {
    navigation.navigate('AddAnimalForm');
  };

  useEffect(() => {
    store.dispatch(
      addAnimal('1', 'Dog', 'Labrador', 'Brown', 'https://images.rawpixel.com/image_png_800/czNmcy1wcml2YXRlL3Jhd3BpeGVsX2ltYWdlcy93ZWJzaXRlX2NvbnRlbnQvcHUyMzMxNzg4LWltYWdlLXJtNTAzLTAxXzEtbDBqOXFyYzMucG5n.png')
    );
    store.dispatch(
      addAnimal('2', 'Cat', 'Siamese', 'White', 'https://images.rawpixel.com/image_png_800/czNmcy1wcml2YXRlL3Jhd3BpeGVsX2ltYWdlcy93ZWJzaXRlX2NvbnRlbnQvcHUyMzMxNjM2LWltYWdlLTAxLXJtNTAzXzMtbDBqOXFrNnEucG5n.png')
    );
  }, []);

  return (
    <Provider store={store}>
      <NavigationContainer>
        <Stack.Navigator>
          <Stack.Screen
            name="AnimalList"
            component={AnimalList}
            options={{
              title: 'Animal List',
              headerRight: () => (
                <TouchableOpacity
                  style={{ marginRight: 20 }}
                  onPress={handleAddAnimalPress} // Use handleAddAnimalPress function
                >
                  <Text style={{ color: 'white', fontSize: 24 }}>+</Text>
                </TouchableOpacity>
              ),
              headerStyle: {
                backgroundColor: 'rgb(239, 135, 11)',
              },
              headerTintColor: 'white',
            }}
          />
          <Stack.Screen name="AddAnimalForm" component={AddAnimalForm} />
        </Stack.Navigator>
      </NavigationContainer>
    </Provider>
  );
};

export default ListeAnimal;
