import React, { useState } from "react";
import { Text, TextInput, View } from "react-native";
import { StyleSheet, TouchableOpacity } from "react-native";
import FontAwesome5 from 'react-native-vector-icons/FontAwesome5'; // Import de FontAwesome5
import * as Animatable from 'react-native-animatable';

const Home=({navigation})=>{

    const [email,setEmail] = useState(null);
    const [passwort,setPassword] = useState(null);

    const handleLogin = () =>{
        navigation.navigate('homepage')
    }
    const styles = StyleSheet.create({
        container: {
          flex: 1,
          backgroundColor: "white",
          justifyContent: "center",
          padding: 20,
        },
        title: {
          fontSize: 30,
          marginBottom: 20,
          textAlign: "center",
        },
        input: {
          padding: 10,
          backgroundColor: "lightgray",
          borderRadius: 10,
          marginBottom: 15,
        },
        button: {
          backgroundColor: "rgb(239 135 11)",
          borderRadius: 10,
          padding: 15,
          alignItems: "center",
        },
        buttonText: {
          color: "white",
          fontWeight: "bold",
          fontSize: 18,
        },
        option: {
            alignItems: "center",
        },
        optionText: {
            fontSize: 24,
            marginTop: 10,
            color: "rgb(239 135 11)",
        },
        subtitle: {
            fontSize: 18,
            marginBottom: 10,
        },
        left:{
            textAlign:"right",
            backgroundColor
        }
    });

    return(
        <>
        <View style={styles.left}>
            <Text style={styles.optionText}>S'inscrire <FontAwesome5 name="paw" size={20} color="rgb(239 135 11)" /> </Text>
        </View>

        <View style={styles.container}>
            <Animatable.View animation="fadeInDown" style={{ alignItems: 'center' }}>
            <FontAwesome5 name="paw" size={50} color="rgb(239 135 11)" /> 
            <Text style={styles.title}>Bienvenue</Text>
                <TextInput
                style={styles.input}
                placeholder="Votre email"
                onChangeText={(text) => setEmail(text)}
                />
                <TextInput
                style={styles.input}
                secureTextEntry
                placeholder="Votre mot de passe"
                onChangeText={(text) => setPassword(text)}
                />
                <TouchableOpacity style={styles.button} onPress={handleLogin}>
                <Text style={styles.buttonText}>Se connecter</Text> <FontAwesome5 name="paw" size={20} color="#ffffff" /> 
                </TouchableOpacity>

                <Text style={styles.subtitle}>Pas encore de compte?</Text>
                
                <TouchableOpacity
                style={styles.option}
                onPress={() => navigation.navigate("inscription")}
                >
                </TouchableOpacity>
      </Animatable.View>
    </View>
    </>
    )
}

export default Home
