import React from "react"
import {Ionicons} from "@expo/vector-icons"
import { View } from "react-native";

const Stars = ({stars}) =>{
    const etoiles=[];

    for(let i=1;i<=3;i++){
        if(i<=stars){
            etoiles.push(<Ionicons name="ios-star" key={i} size={24} color="red"/>)
        }
        else{
            etoiles.push(<Ionicons name="ios-star-outline" key={i} size={24} color="red"/>)
        }
    }
    return(
        <View style={{flexDirection:"row",align}}>
            {etoiles}
        </View>
    )
}

export default Stars
