export const ADD_ANIMAL = 'ADD_ANIMAL';
export const DELETE_ANIMAL = 'DELETE_ANIMAL';
export const UPDATE_ANIMAL = 'UPDATE_ANIMAL';
export const TOGGLE_FAVORITE = 'TOGGLE_FAVORITE'; 

export const addAnimal = (id, nom, type, couleur, photo) => ({
  type: ADD_ANIMAL,
  payload: { id, nom, type, couleur, photo, isfavorite : false },
});

export const deleteAnimal = (id) => ({
  type: DELETE_ANIMAL,
  payload: { id },
});

export const updateAnimal = (id, newData) => ({
  type: UPDATE_ANIMAL,
  payload: { id, newData },
});
