import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, Image, Modal } from 'react-native';
import axios from 'axios';
import { FontAwesome5 } from '@expo/vector-icons';
import EditAnimalForm from './EditAnimalForm';

const AnimalList = () => {
  const [animals, setAnimals] = useState([]);
  const [selectedAnimal, setSelectedAnimal] = useState(null);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:3000/crud/allAnimal');
      setAnimals(response.data);
    } catch (error) {
      console.error('Error fetching animals:', error);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);

  const handleToggleFavorite = (id) => {
    console.log('Toggling favorite for animal with ID:', id);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/crud/animal/${id}`);
      setAnimals(animals.filter(animal => animal.id !== id));
    } catch (error) {
      console.error('Error deleting animal:', error);
    }
  };

  const handleEdit = (animal) => {
    set
    navigation.navigate('profil', { selectedAnimal: animal })
  };

  const styles = StyleSheet.create({
    container: {
      flexDirection: 'row',
      padding: 10,
      borderBottomWidth: 1,
      backgroundColor: 'white'
    },
    animalInfo: {
      flex: 1,
      marginLeft: 10,
    },
    animalImage: {
      width: 100,
      height: 100,
      borderRadius: 50,
    },
    editButton: {
      backgroundColor: 'orange',
      borderRadius: 5,
      padding: 5,
    },
    deleteButton: {
      backgroundColor: 'orange',
      borderRadius: 5,
      padding: 5,
    },
    title: {
      fontSize: 24,
      marginBottom: 20,
      color: 'rgb(239, 135, 11)',
      fontFamily: 'Arial',
    },
    heartIcon: {
      position: 'absolute',
      top: 5,
      right: 35,
    },
  });

  return (
    <View>
      <FlatList
        data={animals}
        renderItem={({ item }) => (
          <TouchableOpacity onPress={() => console.log('Pressed:', item.nom)}>
            <View style={styles.container}>
              <Image source={{ uri: `http://localhost:3000/${item.image}` }} style={styles.animalImage} />
              <View style={styles.animalInfo}>
                <Text style={styles.title}>{item.nom}</Text>
                <Text>{item.type}</Text>
                <Text>{item.couleur}</Text>
                <View style={styles.buttonsContainer}>
                  <TouchableOpacity style={styles.editButton} onPress={() => handleEdit(item)}>
                    <FontAwesome5 name="pen" size={20} color="white" />
                  </TouchableOpacity>
                  <TouchableOpacity style={styles.deleteButton} onPress={() => handleDelete(item.id)}>
                    <FontAwesome5 name="trash-alt" size={20} color="white" />
                  </TouchableOpacity>
                </View>
              </View>
              <TouchableOpacity style={styles.heartIcon} onPress={() => handleToggleFavorite(item.id)}>
                <FontAwesome5 name="heart" size={20} color={item.isFavorite ? 'red' : 'black'} />
              </TouchableOpacity>
            </View>
          </TouchableOpacity>
        )}
        keyExtractor={(item) => item.id.toString()}
      />
    </View>
  );
};

export default AnimalList;
