import React, { useState, useEffect } from "react";
import { View, Text, FlatList, StyleSheet, Button } from "react-native";
import axios from "axios";

const AppointmentListDoctor = ({ route, navigation }) => {
  const [appointments, setAppointments] = useState([]);
  const [demands, setDemands] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log(route.params.doctorId);

    // Utilisez route.params.doctorId directement, sans déstructuration supplémentaire
    const idDocteur = route.params.doctorId;

    axios
      .get(`http://127.0.0.1:3000/rendezvous/rendezvouslist/${idDocteur}`)
      .then((response) => {
        setAppointments(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Erreur lors de la récupération des rendez-vous :", error);
        setLoading(false);
      });

    // Récupérer les demandes
    axios
      .get(`http://127.0.0.1:3000/rendezvous/rendezvousnonapplist/${idDocteur}`)
      .then((response) => {
        setDemands(response.data);
      })
      .catch((error) => {
        console.error("Erreur lors de la récupération des demandes :", error);
      });
  }, [route.params]);

  const approveAppointment = (id) => {
    // Approuver un rendez-vous en appelant l'API backend
    axios
      .post(`http://127.0.0.1:3000/approbation/${id}`)
      .then((response) => {
        // Mettre à jour les rendez-vous ou gérer d'autres actions si nécessaire
      })
      .catch((error) => {
        console.error("Erreur lors de l'approbation du rendez-vous :", error);
      });
  };

  const renderItem = ({ item }) => (
    <View style={styles.appointmentItem}>
      <Text style={styles.patientName}>
        {item.patient.name} {item.patient.firstname}
      </Text>
      <Text style={styles.appointmentDescription}>{item.description}</Text>
      <Text style={styles.appointmentTime}>
        {item.date} à {item.heureStart}
      </Text>
      {/* Bouton d'approbation */}
      <Button
        title="Approuver"
        onPress={() => approveAppointment(item._id)}
      />
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Vos rendez-vous</Text>
      {loading ? (
        <Text>Chargement...</Text>
      ) : (
        <>
          <View style={styles.yellowBackground}>
            <FlatList
              data={appointments}
              renderItem={renderItem}
              keyExtractor={(item) => item._id}
            />
          </View>
          <View style={styles.whiteBackground}>
            <FlatList
              data={demands}
              renderItem={renderItem}
              keyExtractor={(item) => item._id}
            />
          </View>
          {appointments.length === 0 && demands.length === 0 && (
            <Text>Aucun rendez-vous</Text>
          )}
        </>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
    textAlign: "center",
  },
  appointmentItem: {
    marginBottom: 20,
    padding: 10,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 10,
  },
  patientName: {
    fontSize: 18,
    fontWeight: "bold",
  },
  appointmentDescription: {
    fontSize: 16,
    marginTop: 5,
  },
  appointmentTime: {
    fontSize: 14,
    marginTop: 5,
    color: "#666",
  },
  yellowBackground: {
    backgroundColor: "yellow",
    marginBottom: 20,
  },
  whiteBackground: {
    backgroundColor: "white",
  },
});

export default AppointmentListDoctor;
