import React, {useState} from 'react';
import { View, Text, FlatList, Button,StyleSheet } from 'react-native';
import { connect } from 'react-redux';
import { deleteAnimal } from '../Crud';
import EditAnimalForm from './EditUserForm'; 

const AnimalList = ({ users, dispatch }) => {
  const [editingAnimal, setEditingAnimal] = useState(null);

  const handleEdit = (animal) => {
    setEditingAnimal(animal); 
  };

  const handleCancelEdit = () => {
    setEditingAnimal(null); 
  };

  const styles = StyleSheet.create({
        main_container: {
            textAlign: "center",
            alignItems: "center",
            justifyContent: "center", 
        },
        title:{
            color:"red",
            fontSize:"24px"
        },
        grey:{
            backgroundColor:'grey'
        },
        white:{
            backgroundColor:'white'
        },
        deleteButton: {
            backgroundColor: "red",
            paddingVertical: 5,
            paddingHorizontal: 10,
            borderRadius: 3,
        },
        noBorder:{
          border:"none",
          width:"20%"
        },
        noBorderTable:{
          border:"none",
          width:"100%"
        }
  });

  return (
    <View>
      <FlatList
        data={users}
        renderItem={({ item }) => (
            <>
                <table style={styles.noBorderTable}>
                        <tr style={item.id % 2 === 0 ? styles.grey : styles.white}>
                            <td style={styles.noBorder}><Text style={styles.textes}>{item.nom}</Text></td>
                            <td style={styles.noBorder}><Text style={styles.textes}>{item.prenom}</Text></td>
                            <td style={styles.noBorder}><Text style={styles.textes}>{item.usernom}</Text></td>
                            <td style={styles.noBorder}><Text style={styles.textes}>{item.age}</Text></td>
                            <td style={styles.noBorder}>
                                <Button title="Delete" onPress={() => dispatch(deleteUser(item.usernom))} />
                            </td>
                            <td style={styles.noBorder}>
                                <Button title="Edit" onPress={() => handleEdit(item)} />
                            </td>
                        </tr>
                </table>
                <View>
                    {editingUser && ( 
                    <View>
                        <EditUserForm user={editingUser} />
                        <Button title="Cancel" onPress={handleCancelEdit} /> 
                    </View>
                    )}
                </View>
            </>
          )}
        keyExtractor={(item) => item.usernom}
      />
    </View>
  );
};

const mapStateToProps = (state) => ({
  users: state.users,
});

export default connect(mapStateToProps)(AnimalList);
