import React, { useState } from 'react';
import { View, Text, FlatList, Button, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { connect } from 'react-redux';
import { deleteAnimal } from '../Crud';
import EditAnimalForm from './EditAnimalForm'; 
import { FontAwesome5 } from '@expo/vector-icons';
import { toggleFavorite } from '../Crud'; 

const AnimalList = ({ animals, dispatch }) => {
  const [editingAnimal, setEditingAnimal] = useState(null);
  const [expandedAnimal, setExpandedAnimal] = useState(null);

  const handleEdit = (animal) => {
    setEditingAnimal(animal); 
  };

  const handleCancelEdit = () => {
    setEditingAnimal(null); 
  };

  const handleExpand = (animal) => {
    setExpandedAnimal(animal);
  };

  const handleCloseExpand = () => {
    setExpandedAnimal(null);
  };

  const handleToggleFavorite = (animal) => {
    dispatch(toggleFavorite(animal.id)); 
  };

  const styles = StyleSheet.create({
    container: {
      flexDirection: 'row',
      padding: 10,
      borderBottomWidth: 1,
      borderBottomColor: '#ccc',
    },
    animalInfo: {
      flex: 1,
      marginLeft: 10,
    },
    animalImage: {
      width: 100,
      height: 100,
      borderRadius: 50,
    },
    editButton: {
      position: 'absolute',
      top: 5,
      right: 5,
    },
    expandContainer: {
      padding: 20,
      borderBottomWidth: 1,
      borderBottomColor: '#ccc',
    },
    expandedInfo: {
      fontSize: 18,
      marginBottom: 10,
    },
    heartIcon: {
      position: 'absolute',
      top: 5,
      right: 35, 
    },
    title: {
      fontSize: 24,
      marginBottom: 20,
      color: "rgb(239, 135, 11)",
      fontFamily: "Arial", 
    }
  });

  return (
    <FlatList
      data={animals}
      renderItem={({ item }) => (
        <>
          <TouchableOpacity onPress={() => handleExpand(item)}>
            <View style={styles.container}>
              <Image source={{ uri: item.photo }} style={styles.animalImage} />
              <View style={styles.animalInfo}>
                <Text style={styles.title}>{item.nom}</Text>
                <Text>{item.type}</Text>
                <Text>{item.couleur}</Text>
              </View>
              <TouchableOpacity style={styles.editButton} onPress={() => handleEdit(item)}>
                <FontAwesome5 name="pen" size={20} color="black" />
              </TouchableOpacity>
              <TouchableOpacity style={styles.heartIcon} onPress={() => handleToggleFavorite(item)}>
                <FontAwesome5 name="heart" size={20} color={item.isFavorite ? "red" : "black"} />
              </TouchableOpacity>
            </View>
          </TouchableOpacity>
          {expandedAnimal && expandedAnimal.id === item.id && (
            <View style={styles.expandContainer}>
              <Text style={styles.title}>Name: {item.nom}</Text>
              <Text style={styles.expandedInfo}>Type: {item.type}</Text>
              <Text style={styles.expandedInfo}>Color: {item.couleur}</Text>
              <Button title="Delete" onPress={() => dispatch(deleteAnimal(item.id))} />
              <TouchableOpacity style={styles.heartIcon}>
                <FontAwesome5 name="heart" size={20} color={item.isfavorite ? "red" : "white"} />
              </TouchableOpacity>
              <Button title="Edit" onPress={() => handleEdit(item)} />
              <Button title="Close" onPress={handleCloseExpand} />
            </View>
          )}
        </>
      )}
      keyExtractor={(item) => item.id.toString()}
    />
  );
};

const mapStateToProps = (state) => ({
  animals: state.animals,
});

export default connect(mapStateToProps)(AnimalList);
