import React, { useState } from "react";
import { View, Text, TouchableOpacity } from "react-native";
import Result from "../../Listes/Result";

const FilmItem = ({navigation}) => {
    // const [visible, setVisible] = useState({});

    const Data = [
        {
            id: 1,
            title: "Pixel1",
            subtitle: "Pixel",
            descri: "Piscel is the most featureful phone ever",
            stars: 0
        },
        {
            id: 2,
            title: "Samsung",
            subtitle: "samsung",
            descri: "Samsung is the most powerful phone ever",
            stars: 3
        },
        {
            id: 3,
            title: "iphone",
            subtitle: "iphone",
            descri: "iphone is the most useful phone ever",
            stars: 2
        }
    ];
    // setVisible({ ...visible, [phone.id]: !visible[phone.id] })}
    // visible[phone.id] && <Result title={phone.title} subtitle={phone.subtitle} descri={phone.descri} stars={phone.stars} />}

    const handleSend = (datas) =>{
        navigation.navigate("Product Details", {datas})
    }

    return (
        <View>
            {Data.map((phone) => (
                <View key={phone.id}>
                    <TouchableOpacity style={{ flex: 1, flexDirection: "row", marginBottom: 20 }} onPress={() => {handleSend(phone);}}>
                        <View style={{ flex: 3, backgroundColor: phone.id % 2 === 0 ? "green" : "blue",alignItems: "center",
            justifyContent: "center",  }}>
                            <Text style={{ textAlign: "center", color: "white" }}>{phone.title}</Text>
                        </View>
                        <View style={{ flex: 3, textAlign: "center", backgroundColor: "white" }}>
                            <Text style={{ textAlign: "center" }}>{phone.subtitle}</Text>
                            <Text style={{ textAlign: "center" }}>{phone.descri}</Text>
                            <Text style={{ textAlign: "center" }}>{phone.stars} Ã©toiles</Text>
                        </View>
                    </TouchableOpacity>
                </View>
            ))}
        </View>
    );
};

export default FilmItem;
