import React, { useState } from "react";
import { View, Text, TextInput, Button, Alert } from "react-native";
import axios from "axios";

const LoginDoctorScreen = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const token = localStorage.getItem('jwt');
    if(token){
        navigate('/home');
    }

    

    //Gestionnaire d'évenement lors de la soumission du formulaire
    const handleSubmit = (e) => {
        e.preventDefault();

        const formData = {
            email : email,
            password : password
        }
        //Configuration de axios pour resoudre les problème CROSS
        axios.defaults.withCredentials = true;

        axios.post('http://localhost:8000/api/auth/login', formData)
        .then((response) => {
            console.log(response.data)
            const {token, role2, id, idrole,role} = response.data;
           localStorage.setItem('jwt', token);
           localStorage.setItem('role2', role2);
           localStorage.setItem('role', role);
           localStorage.setItem('id', id);
           localStorage.setItem('idrole', idrole);

            // Cookies.set('jwt', token)
            navigate('/home');
        })
        .catch((error) => {
            console.error(error);
        })
    }
    const idrole = localStorage.getItem('idrole');
    console.log(idrole);
  return (
    <View>
    <Text>Votre email :</Text>
        <TextInput
          style={{ padding: 10, backgroundColor: "lightgray", borderRadius: 10, marginBottom: 10 }}
          placeholder="fabiola@fab.com"
          onChangeText={(text) => setEmail(text)}
        />
      <Text>Votre mot de passe:</Text>
      <TextInput
        style={{ padding: 10, backgroundColor: "lightgray", borderRadius: 10, marginBottom: 10 }}
        secureTextEntry
        onChangeText={(text) => setPassword(text)}
      />
      <Button title="Se connecter" onPress={handleLogin} />
    </View>
  );
};

export default LoginDoctorScreen;
