import React, { useEffect, useState } from 'react';
import { View, Text } from 'react-native';
import axios from 'axios';

const UserProfile = ({ route }) => {
  const [userData, setUserData] = useState(null); // Initialize userData as null

  useEffect(() => {
    const { doctorId } = route.params; // 

    // Make a GET request to fetch user data based on doctorId
    axios.get(`http://localhost:3000/doctor/profil/${doctorId}`)
      .then((response) => {
        // Set the fetched user data to the state
        setUserData(response.data);
      })
      .catch((error) => {
        // Handle errors appropriately
        console.error(error);
      });
  }, [route.params]); // Watch for changes in route.params

  return (
    <View>
      {userData ? (
        <View>
          <Text>Nom: {userData.name} {userData.first}</Text>
          <Text>Pr√©nom: {userData.firstname}</Text>
          {/* Display other user information here */}
        </View>
      ) : (
        <Text>Chargement en cours...</Text>
      )}
    </View>
  );
};

export default UserProfile;
