import React,{useState} from "react";
import FilmData from "../../Helpers/FilmData";
import { View,Text } from "react-native";
import { StyleSheet } from "react-native";

const FilmItem = (navigation) => {
    const [visible,setVisible] = useState(false)
    const Data = [
        {
            id:1,
            title:"Pixel1",
            subtitle:"Pixel",
            descri:"Piscel is the most featureful phone ever",
            stars:0
        },
        {
            id:2,
            title:"Samsung",
            subtitle:"samsung",
            descri:"Samsung is the most powerful phone ever",
            stars:3
        },
        {
            id:3,
            title:"iphone",
            subtitle:"iphone",
            descri:"iphone is the most useful phone ever",
            stars:2
        }
    ]

    return (
        <View>
            {Data.map((phone) => (
                <View key={phone.id} style={{flex:1, flexDirection:"row"}} onPress={()=>{setVisible(!visible)}}> 
                    <View style={{flex:2, backgroundColor: phone.id % 2 === 0? "green": "blue"}}>
                        <Text style={{textAlign:"center", color:"white"}}>{phone.title}</Text>
                    </View>
                    <View style={{flex:4,textAlign:"center"}}>
                            <Text style={{textAlign:"center"}}>{phone.subtitle}</Text>
                            <Text style={{textAlign:"center"}}>{phone.descri}</Text>
                            <Text style={{textAlign:"center"}}>{phone.stars} Ã©toiles</Text>
                    </View>
                </View>
            ))}
        </View>
    );
};

export default FilmItem;
