import React, { useState } from 'react';
import { View, TouchableOpacity, Image, StyleSheet, Text } from 'react-native';

const Card = ({ suit, value }) => {
  const [currentSuit, setCurrentSuit] = useState(suit);

  const changeSuit = () => {
    switch (currentSuit) {
      case 'Carreau':
        setCurrentSuit('Coeur');
        break;
      case 'Coeur':
        setCurrentSuit('Pique');
        break;
      case 'Pique':
        setCurrentSuit('Trèfle');
        break;
      case 'Trèfle':
        setCurrentSuit('Carreau');
        break;
      default:
        setCurrentSuit('Carreau');
    }
  };

  return (
    <TouchableOpacity onPress={changeSuit}>
      <View style={styles.cardContainer}>
        <Text style={styles.cardText}>{value}</Text>
        <Text style={styles.cardText}>{currentSuit}</Text>
      </View>
    </TouchableOpacity>
  );
};

const App = () => {
  const [cards, setCards] = useState([
    { suit: 'Carreau', value: '2' },
    { suit: 'Carreau', value: '3' },
    { suit: 'Carreau', value: '4' },
    { suit: 'Carreau', value: '5' },
    { suit: 'Carreau', value: '6' },
    { suit: 'Carreau', value: '7' },
    { suit: 'Carreau', value: '8' },
    { suit: 'Carreau', value: '9' },
    { suit: 'Carreau', value: '10' },
  ]);

  const increaseValue = () => {
    setCards((prevCards) =>
      prevCards.map((card) => {
        if (card.value === 'As') {
          return { ...card, value: '7' };
        } else {
          return { ...card, value: String(parseInt(card.value) + 1) };
        }
      })
    );
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={increaseValue}>
        <View style={styles.centralCell}>
          <Text style={styles.centralCellText}>Click Me!</Text>
        </View>
      </TouchableOpacity>
      <View style={styles.cardsContainer}>
        {cards.map((card, index) => (
          <Card key={`card_${index}`} suit={card.suit} value={card.value} />
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  centralCell: {
    width: 100,
    height: 100,
    backgroundColor: 'lightblue',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 10,
  },
  centralCellText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  cardsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  cardContainer: {
    width: 80,
  }})
