import React, { useEffect, useState } from 'react';
import { View, Text, Image, TouchableOpacity, StyleSheet } from 'react-native';
import axios from 'axios';

const UserProfile = ({ route, navigation }) => {
  const [userData, setUserData] = useState(null);
  const etat = localStorage.getItem('etat');

  useEffect(() => {
    const { doctorId } = route.params;

    axios.get(`http://localhost:3000/doctor/profil/${doctorId}`)
      .then((response) => {
        setUserData(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [route.params]);

  return (
    <View style={styles.container}>
      {userData ? (
        <View style={styles.profileCard}>
          <View style={styles.imageContainer}>
            <Image style={styles.profileImage} source={{ uri: `http://localhost:3000/uploads/${userData.photo}` }} />
          </View>
          <Text style={styles.name}>
            {userData.name} {userData.firstname}
          </Text>
          {etat === 'docteur' ? (
            <Text style={styles.speciality}>{userData.speciality}</Text>
          ) : (
            <>
              <Text style={styles.label}>Poids:</Text>
              <Text style={styles.value}>{userData.weight}</Text>
              <Text style={styles.label}>Date de naissance:</Text>
              <Text style={styles.value}>{userData.dateOfBirth}</Text>
              <Text style={styles.label}>Contact:</Text>
              <Text style={styles.value}>{userData.contact}</Text>
              <Text style={styles.label}>Adresse:</Text>
              <Text style={styles.value}>{userData.address}</Text>
            </>
          )}
          <TouchableOpacity
            style={styles.button}
            onPress={() => {
              navigation.navigate('callScreen', { userData: doctor._id });
            }}
          >
            <Text style={styles.buttonText}>Appeler maintenant</Text>
          </TouchableOpacity>
        </View>
      ) : (
        <Text>Loading...</Text>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    alignItems: 'center',
    justifyContent: 'center',
  },
  profileCard: {
    backgroundColor: '#00bfa6',
    borderRadius: 20,
    padding: 20,
    alignItems: 'center',
  },
  imageContainer: {
    borderRadius: 50,
    overflow: 'hidden',
    marginBottom: 10,
  },
  profileImage: {
    width: 100,
    height: 100,
  },
  name: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  speciality: {
    fontSize: 18,
    fontStyle: 'italic',
    marginBottom: 5,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  value: {
    fontSize: 16,
    marginBottom: 5,
  },
  button: {
    backgroundColor: '#00897b',
    borderRadius: 10,
    paddingVertical: 10,
    paddingHorizontal: 20,
    marginTop: 10,
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold',
  },
});

export default UserProfile;
