import React, { useState } from 'react';
import { View, TouchableOpacity, Image, StyleSheet, Text } from 'react-native';

const Card = ({ value, suit }) => {
  const [card, setCard] = useState({ value, suit });

  const changeSuit = () => {
    let newSuit;
    switch (card.suit) {
      case 'carreau':
        newSuit = 'coeur';
        break;
      case 'coeur':
        newSuit = 'pique';
        break;
      case 'pique':
        newSuit = 'trèfle';
        break;
      case 'trèfle':
        newSuit = 'carreau';
        break;
      default:
        newSuit = card.suit;
    }
    setCard({ ...card, suit: newSuit });
  };

  return (
    <TouchableOpacity onPress={changeSuit}>
      <View style={styles.cardContainer}>
        <Text style={styles.cardText}>{card.value}</Text>
        <Text style={styles.cardText}>{card.suit}</Text>
      </View>
    </TouchableOpacity>
  );
};

const TpCart2 = () => {
  const [cards, setCards] = useState([
    { id: 'C0', value: 1, suit: 'carreau' },
    { id: 'C1', value: 2, suit: 'carreau' },
    { id: 'C2', value: 3, suit: 'carreau' },
    { id: 'C3', value: 4, suit: 'carreau' },
    { id: 'C4', value: 5, suit: 'carreau' },
    { id: 'C5', value: 6, suit: 'carreau' },
    { id: 'C6', value: 7, suit: 'carreau' },
    { id: 'C7', value: 8, suit: 'carreau' },
  ]);

  const changeValue = () => {
    const newCards = cards.map((card) => {
      if (card.id === 'C7') {
        return { ...card, value: 7 };
      } else if (card.id === 'C0') {
        return { ...card, value: (card.value + 1) % 8 || 1 };
      }
      return card;
    });
    setCards(newCards);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={changeValue}>
        <View style={styles.table}>
          {cards.map((card) => (
            <Card key={card.id} value={card.value} suit={card.suit} />
          ))}
        </View>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
  },
  table: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  cardContainer: {
    width: 80,
    height: 120,
    justifyContent: 'center',
    alignItems: 'center',
    borderWidth: 1,
    borderColor: 'black',
    borderRadius: 10,
    margin: 5,
  },
  cardText: {
    fontSize: 18,
  },
});

export default TpCart2;
