import React, { useState } from 'react';
import { View, TouchableOpacity, Text, StyleSheet } from 'react-native';

// Définition des cartes
const cards = [
  { id: 0, value: 'A', suit: 'carreau' },
  { id: 1, value: '2', suit: 'carreau' },
  { id: 2, value: '3', suit: 'carreau' },
  { id: 3, value: '4', suit: 'carreau' },
  { id: 4, value: '5', suit: 'carreau' },
  { id: 5, value: '6', suit: 'carreau' },
  { id: 6, value: '7', suit: 'carreau' },
  { id: 7, value: '8', suit: 'carreau' },
  { id: 8, value: '9', suit: 'carreau' },
  { id: 9, value: '10', suit: 'carreau' },
  { id: 10, value: 'J', suit: 'carreau' },
  { id: 11, value: 'Q', suit: 'carreau' },
  { id: 12, value: 'K', suit: 'carreau' }
];

const TpCart2 = () => {
  const [cardsState, setCardsState] = useState(cards);

  // Fonction pour déplacer les cartes
  const moveCards = () => {
    setCardsState(prevCards => {
      const movedCards = [...prevCards];
      // Déplacer chaque carte d'une position vers la gauche (en bouclant)
      for (let i = 0; i < movedCards.length; i++) {
        const cardValue = parseInt(movedCards[i].value);
        if (cardValue < 14) {
          const newValue = cardValue === 13 ? 7 : cardValue + 1;
          movedCards[i].value = newValue.toString();
        }
      }
      return movedCards;
    });
  };

  // Fonction pour changer la famille d'une carte
  const changeSuit = (id) => {
    setCardsState(prevCards => {
      const updatedCards = prevCards.map(card => {
        if (card.id === id) {
          switch (card.suit) {
            case 'carreau':
              card.suit = 'coeur';
              break;
            case 'coeur':
              card.suit = 'pique';
              break;
            case 'pique':
              card.suit = 'trèfle';
              break;
            case 'trèfle':
              card.suit = 'carreau';
              break;
            default:
              break;
          }
        }
        return card;
      });
      return updatedCards;
    });
  };

  return (
    <View style={styles.container}>
      <View style={styles.table}>
        {[0, 1, 2, 3, 4, 5, 6, 7, 8].map(index => (
          <TouchableOpacity key={cardsState[index].id} onPress={() => changeSuit(cardsState[index].id)}>
            <View style={styles.card}>
              <Text>{cardsState[index].value}</Text>
              <Text>{cardsState[index].suit}</Text>
            </View>
          </TouchableOpacity>
        ))}
      </View>
      <TouchableOpacity onPress={moveCards}>
        <View style={styles.centerCell}></View>
      </TouchableOpacity>
      {[9, 10, 11, 12].map(index => (
        <TouchableOpacity key={cardsState[index].id} onPress={() => changeSuit(cardsState[index].id)}>
          <View style={styles.card}>
            <Text>{cardsState[index].value}</Text>
            <Text>{cardsState[index].suit}</Text>
          </View>
        </TouchableOpacity>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff'
  },
  table: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    width: 240
  },
  card: {
    width: 80,
    height: 120,
    justifyContent: 'center',
    alignItems: 'center',
    borderWidth: 1,
    borderColor: 'black',
    margin: 5
  },
  centerCell: {
    width: 80,
    height: 120,
    backgroundColor: 'red',
    margin: 5
  }
});

export default TpCart2;
