import React from 'react';
import { View, Text, FlatList, Button,StyleSheet } from 'react-native';
import { connect } from 'react-redux';
import { deleteUser } from '../Crud';
import EditUserForm from './EditUserForm'; 

const UserList = ({ users, dispatch }) => {
  const [editingUser, setEditingUser] = useState(null);

  const handleEdit = (user) => {
    setEditingUser(user); // Set the editingUser state to the user being edited
  };

  const handleCancelEdit = () => {
    setEditingUser(null); // Clear the editingUser state to exit edit mode
  };

  const styles = StyleSheet.create({
        main_container: {
            textAlign: "center",
            alignItems: "center",
            justifyContent: "center", 
        },
        title:{
            color:"red",
            fontSize:"24px"
        },
        grey:{
            backgroundColor:'grey'
        },
        white:{
            backgroundColor:'white'
        },
        deleteButton: {
            backgroundColor: "red",
            paddingVertical: 5,
            paddingHorizontal: 10,
            borderRadius: 3,
        }
  });

  return (
    <View>
      <FlatList
        data={users}
        renderItem={({ item }) => (
            <>
                <table>
                        <tr style={item.id % 2 === 0 ? styles.grey : styles.white}>
                            <td><Text style={styles.textes}>{item.nom}</Text></td>
                            <td><Text style={styles.textes}>{item.prenom}</Text></td>
                            <td><Text style={styles.textes}>{item.usernom}</Text></td>
                            <td><Text style={styles.textes}>{item.age}</Text></td>
                            <td>
                                <Button title="Delete" onPress={() => dispatch(deleteUser(item.usernom))} />
                            </td>
                        </tr>
                </table>
                <View>
                    {editingUser && ( 
                    <View>
                        <EditUserForm user={editingUser} />
                        <Button title="Cancel" onPress={handleCancelEdit} /> 
                    </View>
                    )}
                </View>
            </>
          )}
        keyExtractor={(item) => item.usernom}
      />
    </View>
  );
};

const mapStateToProps = (state) => ({
  users: state.users,
});

export default connect(mapStateToProps)(UserList);
