import React, { useEffect, useState } from 'react';
import { View, Text } from 'react-native';
import axios from 'axios';

const UserProfile = () => {
  const [userData, setUserData] = useState(null);
  const userId = localStorage.getItem('id'); // Remarque : vous devez utiliser AsyncStorage pour stocker des données sur l'appareil mobile

  useEffect(() => {
    // Effectuez la requête GET vers votre backend en utilisant Axios
    axios.get(`http://votre-backend.com/profil/${userId}`)
      .then((response) => {
        // Traitez la réponse du serveur ici
        setUserData(response.data);
      })
      .catch((error) => {
        // Gérez les erreurs de manière appropriée
        console.error(error);
      });
  }, [userId]);

  return (
    <View>
      {userData ? (
        <View>
          <Text>Nom: {userData.name}</Text>
          <Text>Prénom: {userData.firstName}</Text>
          {/* Affichez d'autres informations utilisateur ici */}
        </View>
      ) : (
        <Text>Chargement en cours...</Text>
      )}
    </View>
  );
};

export default UserProfile;
