export const ADD_USER = 'ADD_USER';
export const DELETE_USER = 'DELETE_USER';
export const UPDATE_USER = 'UPDATE_USER';

export const addUser = (nom, prenom, age, usernom) => ({
  type: ADD_USER,
  payload: { id, nom, prenom, age, usernom },
});

export const deleteUser = (usernom) => ({
  type: DELETE_USER,
  payload: { usernom },
});

export const updateUser = (usernom, newData) => ({
  type: UPDATE_USER,
  payload: { usernom, newData },
});
