import React, { useState } from "react";
import { View, Text, TextInput, Button, TouchableOpacity, StyleSheet } from "react-native";
import axios from "axios";
import { styles } from "./LoginGabi.style";
import { style } from "../../Input/Input.style";
import COLORS from "../../../theme";

const LoginDoctorScreen = ({ navigation }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      const res = await axios.post("http://127.0.0.1:3000/auth/loginDoctor", {
        email,
        password,
      });

      if (res.status === 200) {
        console.log("Login Response Data:", res.data);

        localStorage.setItem('token',res.data.token)
        localStorage.setItem('id',res.data.id)
        localStorage.setItem('etat',res.data.etat)

        navigation.navigate("home");
      } else {
        Alert.alert("Login Failed", "Invalid email or password");
      }
    } catch (error) {
      console.error(error);
      Alert.alert("Error", "Login failed. Please try again later.");
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Connexion MÃ©decin</Text>
      <TextInput
        style={styles.input}
        placeholder="Votre email"
        onChangeText={(text) => setEmail(text)}
      />
      <TextInput
        style={styles.input}
        secureTextEntry
        placeholder="Votre mot de passe"
        onChangeText={(text) => setPassword(text)}
      />
      <TouchableOpacity
        style={styles.button}
        onPress={handleLogin}
      >
        <Text style={styles.buttonText}>Se connecter</Text>
      </TouchableOpacity>
    </View>
  );
};

