import React, { useState } from "react";
import { Button, StyleSheet, Text, View } from "react-native";

const Exercice1 = ({ navigation }) => {
    const [click, setNumberOfClicks] = useState(0);
    const styles = StyleSheet.create({
        main_container: {
            textAlign: "center",
            backgroundColor: "blue",
            alignItems: "center",
            justifyContent: "center", 
        }

    });

    return (
        <View style={styles.main_container}>
          <View>
            <Text>{click > 0 ? click : 0}</Text>
            <View style={{flex:1,flexDirection:"row"}}>
                <Button title='SOU' style={{flex:3, marginLeft:10, marginRight:10}} onPress={() => click > 0 ? setNumberOfClicks(click - 1) : setNumberOfClicks(0)} />
                <Button title='ADD' style={{flex:3, marginLeft:10, marginRight:10}} onPress={() => setNumberOfClicks(click + 1)} />
            </View>
            <Text>Vous avez cliqu√© {click} fois</Text>
            <Button title='Restart' onPress={() => setNumberOfClicks(0)}/>
            <Button title='Exercice 2' onPress={() => navigation.navigate('Product Navigation')} />
        </View>
        </View>
    );
};

export default Exercice1;
