import React from "react";
import FilmData from "../../Helpers/FilmData";
import { View } from "react-native";
import { StyleSheet } from "react-native";

const FilmItem = (navigation) => {
    const style = StyleSheet.create({
        main_container: {
            height: 190, 
            flexDirection: "row"
        },
        image: {
            width: 120, 
            height: 180, 
            margin: 5,
            backgroundColor:"gray"
        },
        content_container:{
            flex:145,
            margin:5
        }, 
        header_container:{
            flex: 3,
            flexDirection:"row"
        },
        title_text: {
            fontweight:"bold",
            fontsize: 20, 
            flex: 1, 
            flexwrap:'wrap',
            paddingRight: 5
        },
        vote_text: {
            fontweight: 'bold',
            fontsize: 26, 
            color: '#666666'
        }, 
        description_container:{
            flex: 7
        },
        description_text: {
            fontStyle: 'italic',
            color: '#666666'
        },
        date_container: {
            flex: 1
        },
        date_text: {
            textAlign: right,
            fontSize: 14
        }
    })
    return (
        <View>
            {FilmData.map((film) => (
                <View key={film.id} onClick={()=>{navigation.navigate(`/film/${film.id}`)}}> 
                    <Image style = {style.image} source = {{uri:"image"}}/>
                    <View style={style.content_container}>
                        <View style={style.header_container}>
                            <Text style={style.title_text}>{film.title}</Text>
                            <Text style={style.vote_text}>{film.vote_average}</Text>
                        </View>
                        <View style={style.description_container}>
                            <Text style={style.description_text} numberOfLines={6}>{film.overview}</Text>
                        </View>
                        <View style={style.date_container}>
                            <Text style={style.date_text}>Sorti le {film.release_date}</Text>
                        </View>
                    </View>
                </View>
            ))}
        </View>
    );
};

export default FilmItem;
