import React, { useState } from "react";
import { Button, StyleSheet, Text, View} from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { deleteUser } from "./Crud";

const ListUser=({navigation})=>{

    const dispatch = useDispatch();
    const users = useSelector((state) => state.users);
  
    const handleDelete = (userId) => {
      dispatch(deleteUser(userId));
    };

    const liste = [{
        id:1,
        nom:"Rakoto",
        prenom:"Rabe",
        nomUser:"Rak",
        age:13
    },{
        id:2,
        nom:"Rakoto",
        prenom:"Rabe",
        nomUser:"Rak",
        age:13
    },{
        id:3,
        nom:"Rakoto",
        prenom:"Rabe",
        nomUser:"Rak",
        age:13
    }]
    const styles = StyleSheet.create({
        main_container: {
            textAlign: "center",
            alignItems: "center",
            justifyContent: "center", 
        },
        title:{
            color:"red",
            fontSize:"24px"
        },
        grey:{
            backgroundColor:'grey'
        },
        white:{
            backgroundColor:'white'
        },
        
    });
    return(
        <View>
        <View style={styles.main_container}>
            <Text style={styles.title}>User application</Text>
            <Button title="add user" onPress={navigation.navigate('addUser')}/>
        </View>
            {
                liste.map(Test=>(
                <>
                <table>
                    <tr style={Test.id % 2 === 0 ? styles.grey : styles.white}>
                        <td><Text style={styles.textes}>{Test.nom}</Text></td>
                        <td><Text style={styles.textes}>{Test.prenom}</Text></td>
                        <td><Text style={styles.textes}>{Test.nomUser}</Text></td>
                        <td><Text style={styles.textes}>{Test.age}</Text></td>
                        <td>
                            <TouchableOpacity
                                style={styles.deleteButton}
                                onPress={() => handleDelete(Test.id)}
                            >
                                <Text style={styles.deleteButtonText}>Delete</Text>
                            </TouchableOpacity>
                        </td>
                    </tr>
                </table>
                </>
                ))
            }
        </View>
    )
}
export default ListUser
