import React from 'react';
import { View, Text, FlatList, Button } from 'react-native';
import { connect } from 'react-redux';
import { deleteUser } from '../Crud';

const UserList = ({ users, dispatch }) => {
  return (
    <View>
      <FlatList
        data={users}
        renderItem={({ item }) => (
          <View>
            <Text>Nom: {item.nom}</Text>
            <Text>Pr√©nom: {item.prenom}</Text>
            <Text>Age: {item.age}</Text>
            <Text>Usernom: {item.usernom}</Text>
            <Button title="Delete" onPress={() => dispatch(deleteUser(item.usernom))} />
          </View>
        )}
        keyExtractor={(item) => item.usernom}
      />
    </View>
  );
};

const mapStateToProps = (state) => ({
  users: state.users,
});

export default connect(mapStateToProps)(UserList);
