import React, { useState } from "react";
import { Button, StyleSheet, Text, View } from "react-native";

const ListUser=({navigation})=>{

    const liste = [{
        nom:"Rakoto",
        prenom:"Rabe",
        nomUser:"Rak",
        age:13
    },{
        nom:"Rakoto",
        prenom:"Rabe",
        nomUser:"Rak",
        age:13
    },{
        nom:"Rakoto",
        prenom:"Rabe",
        nomUser:"Rak",
        age:13
    }]
    const styles = StyleSheet.create({
        main_container: {
            textAlign: "center",
            alignItems: "center",
            justifyContent: "center", 
        },
        title:{
            color:"red",
            fontSize:"24px"
        },
        grey:{
            backgroundColor:'grey'
        },
        white:{
            backgroundColor:'white'
        },
    });
    return(
        <View>
        <View style={styles.main_container}>
            <Text style={styles.title}>User application</Text>
            <Button title="add user" onPress={navigation.navigate('/addUser')}/>
        </View>
            {
                liste.map(Test=>(
                <>
                <Table>
                    <>

                    </>
                    <View style={Test.id % 2 === 0 ? styles.grey : styles.white}>
                    <Text style={styles.textes}>{Test.nom}</Text>
                    <Text style={styles.textes}>{Test.prenom}</Text>
                    <Text style={styles.textes}>{Test.nomUser}</Text>
                    <Text style={styles.textes}>{Test.age}</Text>
                    </View>
                </Table>
                </>
                ))
            }
        </View>
    )
}
export default ListUser
