import { ADD_ANIMAL, DELETE_ANIMAL, UPDATE_ANIMAL, TOGGLE_FAVORITE } from './Crud';

const initialState = {
  animals: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_ANIMAL:
      return {
        ...state,
        animals: [...state.animals, { ...action.payload }],
      };
    case DELETE_ANIMAL:
      return {
        ...state,
        animals: state.animals.filter(animal => animal.id !== action.payload.id),
      };
    case UPDATE_ANIMAL:
      return {
        ...state,
        animals: state.animals.map(animal =>
          animal.id === action.payload.id ? { ...animal, ...action.payload.newData } : animal
        ),
      };
    case TOGGLE_FAVORITE:
      return {
        ...state,
        animals: state.animals.map(animal =>
          animal.id === action.payload.id ? { ...animal, isFavorite: !animal.isFavorite } : animal
        ),
      };
    default:
      return state;
  }
};

export default reducer;
