import React from "react";
import { View, Text, TouchableOpacity, StyleSheet } from "react-native";
import ButtonRegister from "../components/Buttons/ButtonRegister";

const Login = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Se connecter en tant que</Text>
      <View style={styles.buttonContainer}>
        <ButtonRegister
          title="Docteur"
          onPress={() => navigation.navigate("login")}
        />
        <ButtonRegister
          title="Patient"
          onPress={() => navigation.navigate("loginPatient")}
        />
      </View>
      <Text style={styles.subtitle}>Vous n'avez pas encore de compte?</Text>
      <View style={styles.buttonContainer}>
        <ButtonRegister
          title="Docteur"
          onPress={() => navigation.navigate("inscriptionDoctor")}
        />
        <ButtonRegister
          title="Patient"
          onPress={() => navigation.navigate("inscriptionPatient")}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#fff",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  buttonContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    marginBottom: 20,
  },
  subtitle: {
    fontSize: 18,
    marginBottom: 10,
  },
});

export default Login;
