import React from "react";
import { View, Text, Button } from "react-native";
import Stars from "../Exercice/exos/Etoiles";
const Result = ({route}) => {
    const {datas} = route.params
    return (
        <View style={{height:"50vh"}}>
            <View style={{backgroundColor:"blue",fontStyle:"bold", width:"100%", height:"50%", justifyContent:"center", alignItems:"center"}}>
                <Text style={{textAlign:"center", color:"white", fontSize:32}}>{datas.title}</Text>
            </View>
            <View style={{backgroundColor:"black", justifyContent:"center", alignItems:"center",height:"50%"}}>
                    <Text style={{textAlign:"center", color:"white"}}>{datas.subtitle}</Text>
                    <Text style={{textAlign:"center", color:"white"}}>{datas.descri}</Text>
                    <Stars stars={datas.stars}/>
                    <Button style={{backgroundColor:"yellow"}}>Commenter</Button>
            </View>
        </View>
    );
};

export default Result;
