import { ADD_USER, DELETE_USER, UPDATE_USER } from './Crud';

const initialState = {
  users: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_USER:
      return {
        ...state,
        users: [...state.users, action.payload],
      };
    case DELETE_USER:
      return {
        ...state,
        users: state.users.filter(user => user.usernom !== action.payload.usernom),
      };
    case UPDATE_USER:
      return {
        ...state,
        users: state.users.map(user =>
          user.usernom === action.payload.usernom ? { ...user, ...action.payload.newData } : user
        ),
      };
    default:
      return state;
  }
};

export default reducer;
