import AsyncStorage from "@react-native-async-storage/async-storage";
import React from "react";
import { Text, TouchableOpacity, View, ImageBackground, StyleSheet } from "react-native";
import COLORS from "../theme";
import ButtonRegister from "../components/Buttons/ButtonRegister";

const Login = ({ navigation }) => {
  return (
    <ImageBackground
      source={require("../assets/images/back")} // Add your background image source
      style={styles.backgroundImage}
    >
      <View style={styles.container}>
        <Text style={styles.title}>Se connecter en tant que</Text>
        <View style={styles.buttonContainer}>
          <ButtonRegister
            title="Docteur"
            onPress={() => navigation.navigate("login")}
          />
          <ButtonRegister
            title="Patient"
            onPress={() => navigation.navigate("loginPatient")}
          />
        </View>
        <Text style={styles.subtitle}>Vous n'avez pas encore de compte?</Text>
        <View style={styles.buttonContainer}>
          <ButtonRegister
            title="Docteur"
            onPress={() => navigation.navigate("inscriptionDoctor")}
          />
          <ButtonRegister
            title="Patient"
            onPress={() => navigation.navigate("inscriptionPatient")}
          />
        </View>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center",
  },
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "rgba(255, 255, 255, 0.7)", // Semi-transparent white background
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  buttonContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    marginBottom: 20,
  },
  subtitle: {
    fontSize: 18,
    marginBottom: 10,
  },
});

export default Login;
