import React, { useState } from 'react';
import { View, TextInput, TouchableOpacity, Text, Image, StyleSheet } from 'react-native';
import { connect } from 'react-redux';
import { addAnimal } from '../Crud';
import * as ImagePicker from 'expo-image-picker';

const AddAnimalForm = ({ dispatch, onClose }) => {
  const [nom, setNom] = useState('');
  const [type, setType] = useState('');
  const [couleur, setCouleur] = useState('');
  const [image, setImage] = useState(null);

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: false,
      aspect: [4, 3],
      quality: 1,
      base64: false,
    });

    if (!result.cancelled) {
      setImage(result.uri);
    }
  };

  const handleSubmit = async () => {
    if (!image) {
      console.error('Please select an image');
      return;
    }

    const response = await fetch(image);
    const blob = await response.blob();

    dispatch(addAnimal(nom, type, couleur, blob));

    setNom('');
    setType('');
    setCouleur('');
    setImage(null);
    onClose();
  };

  return (
    <View style={styles.container}>
      <TextInput style={styles.input} placeholder="Nom" value={nom} onChangeText={setNom} />
      <TextInput style={styles.input} placeholder="Type" value={type} onChangeText={setType} />
      <TextInput style={styles.input} placeholder="Couleur" value={couleur} onChangeText={setCouleur} />
      <TouchableOpacity style={styles.imageButton} onPress={pickImage}>
        <Text style={styles.imageButtonText}>SÃ©lectionner une photo</Text>
      </TouchableOpacity>
      {image && <Image source={{ uri: image }} style={styles.imagePreview} />}
      <TouchableOpacity style={styles.editButton} onPress={handleSubmit}>
        <Text>Ajouter</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 10,
  },
  input: {
    borderWidth: 1,
    borderColor: 'gray',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  imageButton: {
    backgroundColor: 'lightblue',
    padding: 10,
    borderRadius: 5,
    alignItems: 'center',
  },
  imageButtonText: {
    color: 'white',
  },
  imagePreview: {
    width: 100,
    height: 100,
    marginTop: 10,
    borderRadius: 5,
  },
  editButton: {
    backgroundColor: 'orange',
    borderRadius: 5,
    padding: 10,
    alignItems: 'center',
  },
});

export default connect()(AddAnimalForm);
