              onPress={() => {
              navigation.navigate('callScreen', { doctorId: doctor._id });
           }}
            >
          <Text>Appeler maintenant</Text>
          </TouchableOpacity>
          </View>
      ) : (
        <Text>Chargement en cours...</Text>
      )}
    </View>
  );
};

export default UserProfile;
